CREATE SCHEMA IF NOT EXISTS DND;
DROP TABLE IF EXISTS DND.TRANSLATION;
CREATE TABLE IF NOT EXISTS DND.TRANSLATION
(
    TRANSLATION_ID INT AUTO_INCREMENT PRIMARY KEY,
    CATEGORY_ID    INT,
    ENGLISH_VALUE  CHARACTER VARYING not null,
    FRENCH_VALUE   CHARACTER VARYING not null,
    CONSTRAINT TRANSLATION_UNIQUE UNIQUE (CATEGORY_ID, ENGLISH_VALUE, FRENCH_VALUE),
    FOREIGN KEY (CATEGORY_ID) REFERENCES DND.MAPPING_FRENCH (MAPPING_ID)
);

